import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Brain, Plus, Layout, ArrowLeft, Check, X, User, BarChart3 } from 'lucide-react';

const API_URL = "http://127.0.0.1:8000";

function App() {
  const [view, setView] = useState('home'); // home, create, study, profile
  const [decks, setDecks] = useState([]);
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);
  
  // Создание
  const [text, setText] = useState("");
  const [title, setTitle] = useState("");
  const [difficulty, setDifficulty] = useState("easy");

  // Тест
  const [cardIdx, setCardIdx] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  useEffect(() => { fetchDecks(); }, []);

  const fetchDecks = async () => {
    const res = await axios.get(`${API_URL}/decks`);
    setDecks(res.data);
  };

  const handleGenerate = async () => {
    setLoading(true);
    const fd = new FormData();
    fd.append('text', text);
    fd.append('title', title);
    fd.append('difficulty', difficulty);
    try {
      await axios.post(`${API_URL}/generate`, fd);
      fetchDecks(); setView('home');
    } catch (e) { alert("Ошибка!"); }
    setLoading(false);
  };

  const startStudy = async (deck) => {
    const res = await axios.get(`${API_URL}/decks/${deck.id}`);
    setCards(res.data);
    setCardIdx(0); setIsAnswered(false); setView('study');
  };

  return (
    <div className="max-w-4xl mx-auto p-6 min-h-screen font-sans">
      {/* Шапка */}
      <header className="flex justify-between items-center mb-10">
        <div className="flex items-center gap-2 cursor-pointer" onClick={() => setView('home')}>
          <Brain className="text-indigo-600 w-8 h-8" />
          <h1 className="text-2xl font-black tracking-tight">FlashMind AI</h1>
        </div>
        <div className="flex gap-4">
          <button onClick={() => setView('profile')} className="p-2 hover:bg-slate-100 rounded-full transition"><User /></button>
          <button onClick={() => setView('create')} className="bg-indigo-600 text-white px-5 py-2 rounded-xl flex items-center gap-2 hover:bg-indigo-700 transition">
            <Plus size={20} /> Создать
          </button>
        </div>
      </header>

      {/* Экран: Профиль */}
      {view === 'profile' && (
        <div className="animate-in fade-in duration-500">
          <h2 className="text-3xl font-bold mb-8">Ваш прогресс</h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
            <div className="bg-white p-6 rounded-3xl shadow-sm border">
              <p className="text-slate-500 mb-1">Всего колод</p>
              <p className="text-4xl font-bold">{decks.length}</p>
            </div>
            <div className="bg-green-50 p-6 rounded-3xl shadow-sm border border-green-100">
              <p className="text-green-600 mb-1">Верных ответов</p>
              <p className="text-4xl font-bold text-green-700">{decks.reduce((a, b) => a + b.correct_answers, 0)}</p>
            </div>
            <div className="bg-red-50 p-6 rounded-3xl shadow-sm border border-red-100">
              <p className="text-red-600 mb-1">Ошибок</p>
              <p className="text-4xl font-bold text-red-700">{decks.reduce((a, b) => a + b.wrong_answers, 0)}</p>
            </div>
          </div>
          <button onClick={() => setView('home')} className="text-indigo-600 font-semibold flex items-center gap-2"><ArrowLeft size={18}/> К списку колод</button>
        </div>
      )}

      {/* Экран: Главная (Список колод) */}
      {view === 'home' && (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          {decks.map(deck => (
            <div key={deck.id} onClick={() => startStudy(deck)} className="bg-white p-6 rounded-[2rem] border-2 border-slate-100 hover:border-indigo-200 cursor-pointer transition-all">
              <div className="flex justify-between items-start mb-4">
                <span className={`px-3 py-1 rounded-full text-xs font-bold uppercase ${
                  deck.difficulty === 'easy' ? 'bg-green-100 text-green-600' : 
                  deck.difficulty === 'medium' ? 'bg-orange-100 text-orange-600' : 'bg-red-100 text-red-600'
                }`}>
                  {deck.difficulty}
                </span>
                <BarChart3 size={18} className="text-slate-300" />
              </div>
              <h3 className="text-xl font-bold mb-4">{deck.title}</h3>
              <div className="flex gap-4 text-sm font-medium">
                <span className="text-green-600">✓ {deck.correct_answers}</span>
                <span className="text-red-400">✗ {deck.wrong_answers}</span>
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Экран: Создание */}
      {view === 'create' && (
        <div className="bg-white p-8 rounded-[2.5rem] shadow-2xl border border-slate-50">
          <h2 className="text-2xl font-bold mb-6 text-center">Настройка новой лекции</h2>
          <input 
            type="text" placeholder="Название темы..."
            className="w-full mb-6 p-4 bg-slate-50 rounded-2xl outline-none focus:ring-2 ring-indigo-200 font-bold"
            value={title} onChange={e => setTitle(e.target.value)}
          />
          
          <div className="mb-8">
            <p className="text-sm font-bold text-slate-400 mb-3 ml-1 uppercase">Сложность и объем</p>
            <div className="flex gap-3">
              {['easy', 'medium', 'hard'].map(lvl => (
                <button
                  key={lvl}
                  onClick={() => setDifficulty(lvl)}
                  className={`flex-1 py-3 rounded-xl font-bold capitalize transition-all ${
                    difficulty === lvl ? 'bg-indigo-600 text-white shadow-lg' : 'bg-slate-100 text-slate-500 hover:bg-slate-200'
                  }`}
                >
                  {lvl === 'easy' ? 'Легко' : lvl === 'medium' ? 'Средне' : 'Хардкор'}
                </button>
              ))}
            </div>
            <p className="text-xs text-slate-400 mt-2 px-1">
              {difficulty === 'easy' ? '~6 простых вопросов' : difficulty === 'medium' ? '~11 детальных вопросов' : '~18 глубоких вопросов'}
            </p>
          </div>

          <textarea 
            placeholder="Вставьте текст конспекта..."
            className="w-full h-48 p-5 bg-slate-50 rounded-2xl mb-6 outline-none focus:ring-2 ring-indigo-200"
            value={text} onChange={e => setText(e.target.value)}
          />
          <button 
            disabled={loading || !text || !title}
            onClick={handleGenerate}
            className="w-full py-5 bg-indigo-600 text-white rounded-2xl font-black text-xl hover:bg-indigo-700 transition-all disabled:opacity-30"
          >
            {loading ? "GigaChat проектирует тест..." : "Создать учебный модуль"}
          </button>
        </div>
      )}

      {/* Экран: Тест (как в прошлом ответе, только с кнопкой "Завершить") */}
      {/* ... (код теста остается прежним, только меняем cards[cardIdx].q на .question) */}
    </div>
  );
}

export default App;
