import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Brain, Plus, Layout, ArrowLeft, Check, X } from 'lucide-react';

// Если бэкенд на другом адресе, замени на http://127.0.0.1:8000
const API_URL = "http://localhost:8000";

function App() {
  const [view, setView] = useState('home'); // home, create, study
  const [decks, setDecks] = useState([]);
  const [currentDeck, setCurrentDeck] = useState(null);
  const [cards, setCards] = useState([]);
  const [loading, setLoading] = useState(false);

  // Состояния для создания
  const [text, setText] = useState("");
  const [title, setTitle] = useState("");

  // Состояния для тренировки (теста)
  const [cardIdx, setCardIdx] = useState(0);
  const [isAnswered, setIsAnswered] = useState(false);
  const [selectedOption, setSelectedOption] = useState(null);

  useEffect(() => { 
    fetchDecks(); 
  }, []);

  const fetchDecks = async () => {
    try {
      const res = await axios.get(`${API_URL}/decks`);
      setDecks(res.data);
    } catch (e) {
      console.error("Ошибка при получении колод:", e);
    }
  };

  const handleGenerate = async () => {
    setLoading(true);
    const fd = new FormData();
    fd.append('text', text);
    fd.append('title', title || "Новая колода");
    try {
      await axios.post(`${API_URL}/generate`, fd);
      setText(""); 
      setTitle("");
      fetchDecks();
      setView('home');
    } catch (e) { 
      alert("Ошибка API при генерации. Проверьте терминал бэкенда."); 
    }
    setLoading(false);
  };

  const startStudy = async (deck) => {
    try {
      const res = await axios.get(`${API_URL}/decks/${deck.id}`);
      // Парсим опции, если они пришли в виде строки или объекта
      const formattedCards = res.data.map(card => ({
        ...card,
        options: typeof card.options === 'string' ? JSON.parse(card.options) : card.options
      }));
      setCards(formattedCards);
      setCurrentDeck(deck);
      setCardIdx(0);
      setIsAnswered(false);
      setSelectedOption(null);
      setView('study');
    } catch (e) {
      alert("Ошибка при загрузке карточек");
    }
  };

  const handleSelectOption = (option) => {
    if (isAnswered) return;
    setSelectedOption(option);
    setIsAnswered(true);
    
    const isCorrect = option === cards[cardIdx].correct;
    // Отправляем результат на бэкенд для системы повторений
    axios.post(`${API_URL}/cards/${cards[cardIdx].id}/review?known=${isCorrect}`);
  };

  const nextQuestion = () => {
    if (cardIdx < cards.length - 1) {
      setCardIdx(cardIdx + 1);
      setIsAnswered(false);
      setSelectedOption(null);
    } else {
      alert("Тест завершен! Отличная работа.");
      setView('home');
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6 min-h-screen">
      {/* Header */}
      <header className="flex justify-between items-center mb-10 border-b pb-4">
        <div className="flex items-center gap-2 cursor-pointer" onClick={() => setView('home')}>
          <Brain className="text-indigo-600 w-8 h-8" />
          <h1 className="text-2xl font-bold italic text-slate-800">FlashMind AI</h1>
        </div>
        {view === 'home' && (
          <button 
            onClick={() => setView('create')} 
            className="bg-indigo-600 text-white px-5 py-2 rounded-xl flex items-center gap-2 hover:bg-indigo-700 shadow-lg shadow-indigo-200 transition"
          >
            <Plus size={20} /> Создать колоду
          </button>
        )}
      </header>

      {/* View: Home (Список колод) */}
      {view === 'home' && (
        <div>
          <h2 className="text-xl font-semibold mb-6 text-slate-600">Ваши учебные материалы</h2>
          {decks.length === 0 ? (
            <div className="text-center py-20 bg-white rounded-3xl border-2 border-dashed border-slate-200">
              <p className="text-slate-400">У вас пока нет колод. Нажмите "Создать колоду", чтобы начать.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {decks.map(deck => (
                <div 
                  key={deck.id} 
                  onClick={() => startStudy(deck)} 
                  className="bg-white p-6 rounded-2xl shadow-sm border border-slate-200 hover:border-indigo-400 hover:shadow-md cursor-pointer transition-all group"
                >
                  <Layout className="text-indigo-500 mb-3 group-hover:scale-110 transition" />
                  <h3 className="font-bold text-xl mb-1 text-slate-800">{deck.title}</h3>
                  <p className="text-slate-400 text-sm">Нажми, чтобы начать тест</p>
                </div>
              ))}
            </div>
          )}
        </div>
      )}

      {/* View: Create (Генерация) */}
      {view === 'create' && (
        <div className="bg-white p-8 rounded-3xl shadow-xl border border-slate-50">
          <button onClick={() => setView('home')} className="flex items-center text-slate-500 mb-6 hover:text-indigo-600 transition">
            <ArrowLeft size={18} className="mr-2" /> Назад
          </button>
          <label className="block text-sm font-medium text-slate-500 mb-1 ml-1">Название темы</label>
          <input 
            type="text" placeholder="Например: Программирование на Python"
            className="w-full mb-6 p-3 border-b-2 border-slate-100 focus:border-indigo-500 outline-none text-2xl font-bold text-slate-800"
            value={title} onChange={e => setTitle(e.target.value)}
          />
          <label className="block text-sm font-medium text-slate-500 mb-1 ml-1">Текст конспекта</label>
          <textarea 
            placeholder="Вставьте текст лекции, чтобы ИИ составил по нему тест..."
            className="w-full h-64 p-5 bg-slate-50 rounded-2xl mb-6 outline-none focus:ring-2 ring-indigo-100 text-slate-700 leading-relaxed"
            value={text} onChange={e => setText(e.target.value)}
          />
          <button 
            disabled={loading || !text}
            onClick={handleGenerate}
            className="w-full py-4 bg-indigo-600 text-white rounded-2xl font-bold text-lg hover:bg-indigo-700 disabled:opacity-50 shadow-xl shadow-indigo-100 transition-all"
          >
            {loading ? "GigaChat анализирует и создает вопросы..." : "Сгенерировать тест за 5 секунд"}
          </button>
        </div>
      )}

      {/* View: Study (Режим интерактивного теста) */}
      {view === 'study' && cards.length > 0 && (
        <div className="flex flex-col items-center w-full max-w-2xl mx-auto">
          <button onClick={() => setView('home')} className="self-start flex items-center text-slate-500 mb-6 hover:text-indigo-600 transition">
            <ArrowLeft size={18} className="mr-2" /> Выйти из теста
          </button>

          {/* Индикатор прогресса */}
          <div className="w-full bg-slate-100 h-3 rounded-full mb-8 overflow-hidden shadow-inner">
            <div 
              className="bg-indigo-500 h-full transition-all duration-500" 
              style={{ width: `${((cardIdx + 1) / cards.length) * 100}%` }}
            ></div>
          </div>

          <div className="bg-white w-full p-8 rounded-[2rem] shadow-2xl border border-slate-50 mb-8">
            <h2 className="text-2xl font-bold text-center mb-10 text-slate-800 leading-tight">
              {cards[cardIdx].q}
            </h2>

            <div className="grid grid-cols-1 gap-4">
              {cards[cardIdx].options?.map((option, index) => {
                let statusStyle = "bg-slate-50 hover:bg-indigo-50 border-slate-200 text-slate-700";
                
                if (isAnswered) {
                  if (option === cards[cardIdx].correct) {
                    statusStyle = "bg-green-100 border-green-500 text-green-800 shadow-sm";
                  } else if (option === selectedOption) {
                    statusStyle = "bg-red-100 border-red-500 text-red-800";
                  } else {
                    statusStyle = "bg-slate-50 border-slate-100 text-slate-300";
                  }
                }

                return (
                  <button
                    key={index}
                    disabled={isAnswered}
                    onClick={() => handleSelectOption(option)}
                    className={`p-5 text-left border-2 rounded-2xl transition-all font-semibold flex items-center justify-between ${statusStyle}`}
                  >
                    <span>{option}</span>
                    {isAnswered && option === cards[cardIdx].correct && <Check size={20} className="text-green-600" />}
                    {isAnswered && option === selectedOption && option !== cards[cardIdx].correct && <X size={20} className="text-red-600" />}
                  </button>
                );
              })}
            </div>
          </div>

          {isAnswered && (
            <button 
              onClick={nextQuestion}
              className="bg-indigo-600 text-white px-10 py-4 rounded-2xl font-bold shadow-2xl shadow-indigo-200 hover:bg-indigo-700 hover:-translate-y-1 transition-all active:scale-95"
            >
              {cardIdx < cards.length - 1 ? "Следующий вопрос" : "Посмотреть результат"}
            </button>
          )}
          
          <p className="mt-8 text-slate-400 font-medium">Вопрос {cardIdx + 1} из {cards.length}</p>
        </div>
      )}
    </div>
  );
}

export default App;
